use std::collections::HashSet;

use anyhow::Result;

const TEST: &'static str = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw";
const PROD: &'static str = "";

fn bitwise() -> Option<usize> {
    return PROD
        .as_bytes()
        .windows(14)
        .position(move |set| {
            let mut data: u32 = 0;
            for &c in set {
                let prev = data;
                data |= 1 << (c - b'a');
                if prev == data {
                    return false;
                }
            }
            return true;
        });
}

fn mapmap() -> Option<usize> {
    return PROD
        .as_bytes()
        .windows(14)
        .position(|c| {
            let mut data: HashSet<u8> = HashSet::new();
            for &c in c {
                if !data.insert(c) {
                    return false;
                }
            }

            return true;
        });
}

fn slowmap() -> Option<usize> {
    return PROD
        .as_bytes()
        .windows(14)
        .position(|c| c.iter().collect::<HashSet<_>>().len() == 14);
}

fn main() -> Result<()> {
    vec![100, 200, 500, 1000, 2000, 3000]
        .iter()
        .for_each(|n| {
            let now = std::time::Instant::now();
            let mut sum = 0;
            for _ in 0..*n {
                sum += bitwise().unwrap();
            }
            println!("bitwise {} {}", now.elapsed().as_micros(), sum);

            let now = std::time::Instant::now();
            let mut sum = 0;
            for _ in 0..*n {
                sum += mapmap().unwrap();
            }
            println!("mapmap {} {}", now.elapsed().as_micros(), sum);
        });

    return Ok(());
}

