use std::{collections::HashSet, thread::JoinHandle};

const PROD: &'static str = "";

fn simple(i: &str, num: usize) -> usize {
    let i = i.as_bytes();
    return i.windows(num)
        .position(|w| {
            return w.iter().collect::<HashSet<_>>().len() == num;
        })
        .map(|x| x + num)
        .unwrap();
}

fn faster(i: &str, num: usize) -> usize {
    let i = i.as_bytes();
    return i.windows(num)
        .position(|w| {
            let mut hash_set = HashSet::new();
            for x in w {
                if !hash_set.insert(x) {
                    return false;
                }
            }
            return true;
        })
        .map(|x| x + num)
        .unwrap();
}

fn fastest(i: &str, num: usize) -> usize {
    let i = i.as_bytes();

    let mut filter = 0u32;
    i.iter()
        .take(num - 1)
        .for_each(|c| filter ^= 1 << (c - b'a'));

    return i.windows(num)
        .position(|w| {
            let first = w[0];
            let last = w[w.len() - 1];
            filter ^= 1 << (last - b'a');
            let res = filter.count_ones() == num as u32;
            filter ^= 1 << (first - b'a');
            res
        })
        .map(|x| x + num)
        .unwrap();
}

fn fastest_multi(i: &str, num: usize, offset: usize, len: usize) -> Option<usize> {
    let i = i[offset..offset+len].as_bytes();

    let mut filter = 0u32;
    i.iter()
        .take(num - 1)
        .for_each(|c| filter ^= 1 << (c - b'a'));

    return i.windows(num)
        .position(|w| {
            let first = w[0];
            let last = w[w.len() - 1];
            filter ^= 1 << (last - b'a');
            let res = filter.count_ones() == num as u32;
            filter ^= 1 << (first - b'a');
            res
        })
        .map(|x| x + num);
}

fn main() {

    let count = 14;
    //let string = std::fs::read_to_string("long").unwrap();
    //let string: &'static String = Box::leak(Box::new(string));
    let string = PROD;

    let now = std::time::Instant::now();
    let mut result = simple(string, count);
    println!("simple({}): {}", result, now.elapsed().as_millis());

    let now = std::time::Instant::now();
    result = faster(string, count);
    println!("faster({}): {}", result, now.elapsed().as_millis());

    let now = std::time::Instant::now();
    result = fastest(string, count);
    println!("fastest({}): {}", result, now.elapsed().as_millis());

    let now = std::time::Instant::now();
    let cpus = 12;
    let regions = string.len() / cpus;
    let mut handles: Vec<JoinHandle<Option<usize>>> = vec![];
    for i in 0..cpus {
        let start = if i == 0 { 0 } else { i * regions - count + 1 };
        let mut len = regions;

        if i == regions - 1 {
            len = string.len();
        }
        handles.push(std::thread::spawn(move || {
            if let Some(x) = fastest_multi(string, count, start, len) {
                return Some(x + start);
            }
            return None;
        }));
    }

    let mut answer = 0;
    for handle in handles {
        let found = handle.join();
        if let Ok(Some(a)) = found {
            answer = a;
            break;
        }
    }

    println!("fastest_multi({:?}): {}", answer, now.elapsed().as_millis());
    println!("final answer: {}", &string[result - count..result])
}

